import React, {useContext} from 'react';
import styled from "styled-components";
import uuid from 'uuid';
import { GlobalContext } from '../context/GlobalState';


const CSS = styled.div`
  margin: 3%;
  padding: 1%;

  h3{
    font-family: 'Lato', sans-serif; 
    font-size: 38px; 
    font-weight: 300; 
    text-align: left;
    color: #dedfe4;
  }

  .button{
    display: inline-flex;
    height: 40px;
    width: 150px;
    border: 2px solid #BFC0C0;
    margin: 20px 20px 20px 20px;
    color: #BFC0C0;
    text-transform: uppercase;
    text-decoration: none;
    font-size: .8em;
    letter-spacing: 1.5px;
    align-items: center;
    justify-content: center;
    overflow: hidden;
  }
  #button-3 {
    position: relative;
    overflow: hidden;
    cursor: pointer;
  }

  #button-3 a {
    position: relative;
    transition: all .45s ease-Out;
  }

  #circle {
    width: 0%;
    height: 0%;
    opacity: 0;
    line-height: 40px;
    border-radius: 50%;
    background: #BFC0C0;
    position: absolute;
    transition: all .5s ease-Out;
    top: 20px;
    left: 70px;
  }

  #button-3:hover #circle {
    width: 200%;
    height: 500%;
    opacity: 1;
    top: -70px;
    left: -70px;
  }

  #button-3:hover a {
    color: #2D3142;
  }

    // margin: 1%;
    // padding: 1%;

    // color: #dedfe4;
    // font-size: 150%;
    // background: transparent;
    // border: solid 1px;
    // border-radius: 10px;


    // &:hover {
    //   background: #3d3d4a;
    //   border: solid transparent 1px;
    //   box-shadow: rgba(0, 0, 0, 0.4) 0px 3px 10px;
    // }
    // &:active {
    //   background: #101010;
    //   border: solid transparent 1px;
    // }



  background: #373641;
  border-radius: 10px;
`;

export const AddBacktest = () => {
  const { addBacktest } = useContext(GlobalContext);

  const onSubmit = (indicator) => {
    const newBacktest = {
      id: uuid(),
      indicator
    }
    addBacktest(newBacktest);
  }

  return (
    <CSS>
      <h3>Create a New Strategy</h3>
      <div className="container-1">
        <div className="btn btn-one">
          <button onClick={() => onSubmit('RSI')}>Relative Strength Index (RSI)</button>
        </div>
      </div>
      <button onClick={() => onSubmit('RSI')}>Relative Strength Index (RSI)</button>
      <button onClick={() => onSubmit('EMA')}>Exponential Moving Average (EMA)</button>
      <button onClick={() => onSubmit('SMA')}>Simple Moving Average (SMA)</button>
      <button onClick={() => onSubmit('BBANDS')}>Bollinger Bands (BB)</button>
    </CSS>
  )
}
