import React, { createContext, useReducer } from 'react';
import AppReducer from './AppReducer';

// Initial state
const initialState = {
  transactions: []
}

// Create context
export const GlobalContext = createContext(initialState);

// Provider component
export const GlobalProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);

  // Actions
  function deleteTransaction(id) {
    dispatch({
      type: 'DELETE_TRANSACTION',
      payload: id
    });
  }

  function addTransaction(transaction) {
    dispatch({
      type: 'ADD_TRANSACTION',
      payload: transaction
    });
  }

  async function runSearch(textSearch) {
    let response = await fetch(`https://www.alphavantage.co/query?function=SYMBOL_SEARCH&keywords=${textSearch.text}&apikey=CQ5JZQYM4DG0OODA`);
    let data = await response.json();
    console.log(data);
    //12/27/20 working here... filter out so resultss reigon only UNITED STATES
    //figure out design elements list or tiles or graph upon searches

    if(data.Note){
      alert("You've maxed the 5 API calls per minute. This project is built using the standard Alpha Vantage API which only allows 5 calls per minute, 500 calls per day.");
    }

    dispatch({
      type: 'RUN_SEARCH',
      payload: textSearch
    });
  }

  return (<GlobalContext.Provider value={{
    transactions: state.transactions,
    deleteTransaction,
    addTransaction,
    runSearch
  }}>
    {children}
  </GlobalContext.Provider>);
}
