import styled from "styled-components";

const CSS = styled.div`


  .main-box{
    color: #898996;
    display:flex;

    padding: 5%;
    margin: 0%;

    .intro-text{  
      padding-top: 8%;
      text-align: center;
      p{
        color: #898996;
        font-size: 22px;
      }
      h4{
        font-size: 30px;
        color: #dedfe4;
      }
    }

    .howto-text{  
      padding-top: 4%;
      width: 50%;
      text-align: center;
      p{
        color: #898996;
        font-size: 16px;
      }
      h4{
        font-size: 180%;
        color: #dedfe4;
      }
    }
    .img-chart{
      z-index:0;
      width: 50%;
      transform: perspective(700px) rotateY(-10deg);
      box-shadow: rgba(0, 0, 0, 0.3) 0px 19px 38px, rgba(0, 0, 0, 0.22) 0px 15px 12px;
    }
    .img-backtest{

      z-index:0;
      width: 50%;
      transform: perspective(700px) rotateY(10deg);
      box-shadow: rgba(0, 0, 0, 0.3) 0px 19px 38px, rgba(0, 0, 0, 0.22) 0px 15px 12px;
    }

  }

  //Try it button CSS
  .btn {
    margin-top: 3%;
    height: 50px;
    line-height: 50px;
    text-align: center;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    -webkit-transition: all .3s ease-out;
    -o-transition: all .3s ease-out;
    transition: all .3s ease-out;

    text-decoration: none;
    display: inline-block;
    &:hover,&:focus {
      outline: none;
      box-shadow: rgba(0, 0, 0, 0) 0px 0px 0px; //invisible box shadow, can only replace outline, not remove
    }
  }

  .text {
    padding: 0 50px;
    visibility: hidden;
  }

  .flip-front, 
  .flip-back {
      position: absolute;
      left: 0;
      top: 0;
      width: 200%;
      height: 100%;
      -webkit-transform-style: flat;
      transform-style: flat;
      -webkit-transition: -webkit-transform .3s ease-out;
      -o-transition: -o-transform .3s ease-out;
      transition: transform .3s ease-out;
      border-radius: 7px;
  }

  .flip-front {
      color: #dedfe4;
      border: 2px solid #65526E;
      background-color: transparent;
      -webkit-transform: rotateX(0deg) translateZ(25px);
      -ms-transform: rotateX(0deg) translateZ(25px);
      -o-transform: rotateX(0deg) translateZ(25px);
      transform: rotateX(0deg) translateZ(25px);
  }

  .flip-back {
    color: #dedfe4;
    background-color: #65526E;
    border: 2px solid #65526E;
    -webkit-transform: rotateX(90deg) translateZ(25px);
    -ms-transform: rotateX(90deg) translateZ(25px);
    -o-transform: rotateX(90deg) translateZ(25px);
    transform: rotateX(90deg) translateZ(25px);
  }

  .btn:hover .flip-front {
      -webkit-transform: rotateX(-90deg) translateZ(25px);
      -ms-transform: rotateX(-90deg) translateZ(25px);
      -o-transform: rotateX(-90deg) translateZ(25px);
      transform: rotateX(-90deg) translateZ(25px);
  }

  .btn:hover .flip-back {
      -webkit-transform: rotateX(0deg) translateZ(25px);
      -ms-transform: rotateX(0deg) translateZ(25px);
      -o-transform: rotateX(0deg) translateZ(25px);
      transform: rotateX(0deg) translateZ(25px);
  }

`;

export const Homepage = () => {
  return (
    <CSS>
      <div className="main-box"> 
        <div className="intro-text">
          <h4>PeakTrades</h4>
          <p>
            Simple, yet powerful tool to run backtests for algorithm trading strategies and to visualize stock market data.
          </p>
          <button href="#" class="btn">
            <span class="text">Text</span>
            <span class="flip-front">Try it ➤</span>
            <span class="flip-back">Run Backtest on SPY ➤</span>
          </button>
        </div>
        <img className="img-chart" src="chart-img.png" alt=""/>
      </div>
      <div className="main-box"> 
        <img className="img-backtest" src="backtest-img.png" alt=""/>
        <div className="howto-text">
          <h4>How Does it Work?</h4>
          <p>
            <br/>
            Search a US listed stock ticker and select a company to visualize their historic stock market data on an 
            interactive candlestick chart.
            <br/><br/>
            Select a{" "}
            <a className="" href="https://www.investopedia.com/terms/t/technicalindicator.asp" rel="noreferrer" target="_blank">technical indicator</a> 
            {" "}to create a customizable{" "}
            <a className="" href="https://www.investopedia.com/terms/b/backtesting.asp" rel="noreferrer" target="_blank">backtest</a> 
            {" "}to visualize the results of your strategy. 
            <br/><br/>
            Create multiple {" "}
            <a className="" href="https://www.investopedia.com/terms/b/backtesting.asp" rel="noreferrer" target="_blank">backtests</a>  
            {" "}and compare your algorithms using different indicators across different stocks.
          </p>
        </div>
      </div>
    </CSS>
  )
}
