import React, {useState, useContext} from 'react'
import { GlobalContext } from '../context/GlobalState';

export const Backtest = ({ backtest }) => {
  const [seriesType, setSeriesType] = useState('open');
  const [timePeriod, setTimePeriod] = useState('14');

  const [maxBags, setMaxBags] = useState(5);
  const [initalBalance, setInitialBalance] = useState(10000);
  const [initalBuyType, setInitalBuyType] = useState('usd');
  const [initalBuyAmount, setInitalBuyAmount] = useState(1500);
  const [buyStrategyValue, setBuyStrategyValue] = useState(30);
  const [sellStrategyValue, setSellStrategyValue] = useState(1);

  const { deleteBacktest } = useContext(GlobalContext);

  /*
    ema: function, symbol, interval,time period, series type,
    Sma: function, symbol, interval,time period, series type,
    rsi: function, symbol, interval,time period, series type

    //do below later
    bba: function, symbol, interval,time period, series type, (optionals: dev up, dev down, move avg type)
    MACD:function, symbol, interval, series type, (optional: fastperiod, slow period, signalperiod)
    OBv: function, symbol, interval
    STOCH: function, symbol, interval, (optionals: fast K period, slow K period, slow D period, slow K ma type, slow D ma type)

    pair settings:
    max bags : int
    inital balance: int
    inital bag cost: int or percent
    strategy: selected already... customize the indicator... depending which one, ask user for buy value(-x%)....rebuy delay:int....
    Sell strategy: GAIN... sell profit %

    optional future: trailing buy/sell... stop loss.... DCA system... average bag values(for sell strategy)


  */

  const dropdownItems = (items) => {
    return items.map(item => <option value={item.value}>{item.name}</option>);
  }

  const onSubmit = () => {

    const paperAPICall = { 
      indicator: backtest.indicator,
      //this kinda optional bc current_symbol is in global state already...
      // symbol: current_symbol,....interval: daily..
      seriesType: seriesType,
      timePeriod: +timePeriod
    };

    const pairSettings = {
      maxBags: +maxBags,
      initalBalance: +initalBalance,
      initalBuyType: initalBuyType,
      initalBuyAmount: +initalBuyAmount,
      buyStrategyValue: +buyStrategyValue,
      sellStrategyValue: +sellStrategyValue
    }
    // Ensure no trivial strategies are submitted
    if(pairSettings.maxBags<1 || pairSettings.initalBalance<1 || pairSettings.initalBuyAmount<1 || pairSettings.sellStrategyValue<0 || pairSettings.timePeriod<1){
      // Bags, balance, timeperiod, buy amount, sell gain% must be positive
      alert("Please enter valid inputs. Use positive integers.");
    }else if(pairSettings.initalBuyType==='percent' && (pairSettings.initalBuyAmount<1 || pairSettings.initalBuyAmount>100)){
      // if(buy type is percent) buy amount must be (0-100]
      alert("Buy amount must be 1-100%");
    }else if (pairSettings.initalBuyAmount>pairSettings.initalBalance){
      // inital buy must be < inital balance
      alert("Inital buys must be less than or equal to inital balance.");
    }

    // Ensure no trivial strategy specific settings are submitted <connect to previous if chain thing>
    if(backtest.indicator==='RSI' && (pairSettings.buyStrategyValue<1 || pairSettings.buyStrategyValue>99)){
      alert("RSI values must be between 1-99");
    }

    console.log(paperAPICall, pairSettings);
  }

  let indicatorSettingDOM = null;
  switch(backtest.indicator){
    case 'RSI':
      indicatorSettingDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='14' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'EMA':
      indicatorSettingDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='9' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'SMA':
      indicatorSettingDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='9' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'BB':
      indicatorSettingDOM = (<div>ema custom</div>);
      break;
    case 'next one here':
      //code
      break;
    default:
      indicatorSettingDOM = (<div>indicaator setting dom default</div>);

  }

  let buyStrategyDOM = null;
  switch(backtest.indicator){
    case 'RSI':
      buyStrategyDOM = (<> 
        {/* Buy Strategy */}
        <label>
          Buy at RSI {"<"}
          <input type='number' placeholder='30' onChange={(e) => setBuyStrategyValue(e.target.value)} />
        </label>
      </>);
      break;
    case 'EMA':
      buyStrategyDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='9' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'SMA':
      buyStrategyDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='9' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'BB':
      buyStrategyDOM = (<div>bb custom</div>);
      break;
    case 'next one here':
      //code
      break;
    default:
      buyStrategyDOM = (<div>sell strat dom default</div>);

  }

  return (
    <div className='row mx-4 my-2'>
      <div className='card w-25'>
        {/* Beginning of Settings */}
        {backtest.indicator} Settings

        {/* series type */}
        <label>
          Select Series Type:
          <select onChange={(e) => setSeriesType(e.target.value)}>
            {dropdownItems([
              {name:'open', value:'open'},
              {name:'close', value:'close'},
              {name:'high', value:'high'},
              {name:'low', value:'low'}])}
          </select>
        </label>

        {indicatorSettingDOM}

        <br/>

        <label>Paper Bot Settings</label>
        {/* Max number bags */}
        <label>
          Max number of bags:
          <input type='number' placeholder='5' onChange={(e) => setMaxBags(e.target.value)} />
        </label>

        {/* Inital balance */}
        <label>
        Inital balance (USD):
          <input type='number' min='0' placeholder='10000' onChange={(e) => setInitialBalance(e.target.value)} />
        </label>

        {/* Inital buy (type/amount) */}
        <label>
        Inital buy type:
          <select onChange={(e) => setInitalBuyType(e.target.value)}>
            {dropdownItems([
                {name:'($) USD', value:'usd'},
                {name:'(%) Percent of Balance', value:'percent'},
                {name:'(#) Number of Stocks', value:'numStocks'}])}
            </select>
        </label>
        <label>
        Inital buy amount:
          <input type='number' placeholder='1500' onChange={(e) => setInitalBuyAmount(e.target.value)} />
        </label>

        {buyStrategyDOM}

        {/* Sell Strategy */}
        <label>
          Sell at 
          <input type='number' placeholder='1' onChange={(e) => setSellStrategyValue(e.target.value)} />
          % profit.
        </label>

        {/* submit button */}
        <button onClick={() => onSubmit()} className="btn-secondary w-25">Calculate</button>


        {/* close button */}
        <button onClick={() => deleteBacktest(backtest.id)} className="btn-primary w-25">x</button>
      </div>
      {/* end of settings, beginning of results */}

      <div className='card w-75'>results baby</div>

      {/* End of results */}
    </div>
  );
}
