import React, {useContext} from 'react';
import styled from "styled-components";
import uuid from 'uuid';
import { GlobalContext } from '../context/GlobalState';


const CSS = styled.div`
  margin: 3%;
  padding: 1%;

  h3{
    font-family: 'Lato', sans-serif; 
    font-size: 38px; 
    font-weight: 300; 
    text-align: left;
    color: #dedfe4;
  }

  .btn {
    font-size: 135%;
    font-family: 'Lato', sans-serif; 
    text-align: center;
    // width: 100%;
    // height: 33%;
    // display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
    color: white;
    width: 20%;
    line-height: 90px;
    padding: none;
    margin: 1%;
    padding: 0% 1%;
    transition: all 0.3s;
    span {
      transition: all 0.3s;
      tranform: scale(1, 1);
    }
    color: white; !important
  }

  .btn::before, .btn::after {
    content: '';
    position: absolute;
    transition: all 0.3s;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
  }

  .btn-one::before {
    left: 4px;
    z-index: 1;

    opacity: 0;
    background: rgba(255, 255, 255, 0.1);
    transform: scale(0.1, 1);
  }

  .btn-one:hover::before {
    opacity: 1;
    transform: scale(1, 1);
  }

  .btn-one::after {
    transition: all 0.3s;
    border: 1px solid rgba(255, 255, 255, 0.5);
  }

  .btn-one:hover::after {
    transform: scale(1, .1);
    opacity: 0;
  }
  .white{
    color: white;
  }
  .plus-icon{
    filter: invert(1);
    width: 15%;
  }

  background: #373641;
  border-radius: 10px;
`;

export const AddBacktest = () => {
  const { addBacktest } = useContext(GlobalContext);

  const onSubmit = (indicator) => {
    const newBacktest = {
      id: uuid(),
      indicator
    }
    addBacktest(newBacktest);
  }

  return (
    <CSS>
      <h3>Create a New Strategy</h3>
      <button className="btn btn-one" onClick={() => onSubmit('RSI')}>
        <img className="plus-icon" src="https://img.icons8.com/pastel-glyph/64/000000/plus--v1.png" alt=""/>
        <tspan className="">  </tspan><tspan className="white">Relative Strength Index (RSI)</tspan>
      </button>
      <button className="btn btn-one" onClick={() => onSubmit('EMA')}><tspan className="white">Exponential Moving Average (EMA)</tspan></button>
      <button className="btn btn-one" onClick={() => onSubmit('SMA')}><tspan className="white">Simple Moving Average (SMA)</tspan></button>
      <button className="btn btn-one" onClick={() => onSubmit('BBANDS')}><tspan className="white">Bollinger Bands (BB)</tspan></button>
    </CSS>
  )
}
