import React, { createContext, useReducer } from 'react';
import AppReducer from './AppReducer';

// Initial state
const initialState = {
  transactions: [],
  symbol_list: [], 
  symbolToGraph: ''
}

// Create context
export const GlobalContext = createContext(initialState);

// Provider component
export const GlobalProvider = ({ children }) => {
  const [state, dispatch] = useReducer(AppReducer, initialState);

  // Actions
  function deleteTransaction(id) {
    dispatch({
      type: 'DELETE_TRANSACTION',
      payload: id
    });
  }

  function addTransaction(transaction) {
    dispatch({
      type: 'ADD_TRANSACTION',
      payload: transaction
    });
  }

  async function stockSearch(new_symbol_list) {


    //update state of web app (official symbol_list)
    dispatch({
      type: 'STOCK_SEARCH',
      payload: new_symbol_list
    });
  }

  function selectSymbol(selectedSymbol) {
    console.log("hi ben, you have selected ", selectedSymbol);
    //API call here using `selectedSymbol`


    dispatch({
      type: 'DISPLAY_GRAPHS',
      payload: selectedSymbol
    });
  }

  return (<GlobalContext.Provider value={{
    transactions: state.transactions,
    symbol_list: state.symbol_list,
    deleteTransaction,
    addTransaction,
    stockSearch,
    selectSymbol
  }}>
    {children}
    <script src="https://cdn.jsdelivr.net/npm/bootstrap@5.0.0-beta1/dist/js/bootstrap.bundle.min.js" integrity="sha384-ygbV9kiqUc6oa4msXn9868pTtWMgiQaeYH7/t7LECLbyPA2x65Kgf80OJFdroafW" crossOrigin="anonymous"></script>

  </GlobalContext.Provider>);
}
