import React, { useContext, useState } from 'react';
import * as d3 from "d3";

import "../chart.css";

import Chart from "../components/chart-components/Chart";

import { GlobalContext } from '../context/GlobalState';

export const Balance = () => {
  //grab global variables/functions to call in this file
  const { transactions, graphData_AV_API } = useContext(GlobalContext);

  const amounts = transactions.map(transaction => transaction.amount);

  const total = amounts.reduce((acc, item) => (acc += item), 0).toFixed(2);

  //temp graph d3 example--------------------------
  const chart_width = window.screen.availWidth*2/3;
  const chart_height = window.screen.availHeight*2/3;

  const randomOne = (weight = 1) => {
    return (Math.random() + Math.random() - 1) * weight;
  };
  const generateData = () => {
    const length = Math.round(Math.random() * 90) + 10;

    // initial values
    const seed_close = Math.random() * 150 + 50;
    let previous_close = seed_close;
    let previous_volume = Math.random() * 300 + 10;
    let trend = Math.floor(Math.random() * 2) * 2 - 1;

    // calculate each bar
    return d3.range(length).map((item, i) => {
      const open = previous_close * (1 + randomOne(0.1));
      const close = open * (1 + randomOne(0.2) * trend);
      const high = Math.max(open, close) * (1 + randomOne(0.1));
      const low = Math.min(open, close) * (1 - randomOne(0.1));
      const volume = previous_volume * (1 + randomOne(0.5));

      previous_close = close;
      trend = Math.floor(Math.random() * 2) * 2 - 1;

      return {
        time: i,
        open,
        high,
        low,
        close,
        volume
      };
    });
  };

  const processGraphData = () => {//currently default, daily, compact(100)
    let xAxis_times = [];
    let graphDataProcessed = [];
    for(let myDate in graphData_AV_API['Time Series (Daily)']){
      xAxis_times.push(myDate);
      graphDataProcessed.push({
        "open":graphData_AV_API['Time Series (Daily)'][myDate]['1. open'],
        "close":graphData_AV_API['Time Series (Daily)'][myDate]['4. close'],
        "high":graphData_AV_API['Time Series (Daily)'][myDate]['2. high'],
        "low":graphData_AV_API['Time Series (Daily)'][myDate]['3. low'],
        "volume":graphData_AV_API['Time Series (Daily)'][myDate]['6. volume']
      });
    }
     // console.log(data['Meta Data']['2. Symbol']);
    // console.log(data['Time Series (Daily)']);
    // for(let x in data['Time Series (Daily)']){
    //   console.log(x,data['Time Series (Daily)'][x]);
    // }

    const length = xAxis_times.length;
    // calculate each bar
    return d3.range(length).map((item, i) => {
      return {
        time: i,
        open: +graphDataProcessed[i]['open'],
        high: +graphDataProcessed[i]['high'],
        low: +graphDataProcessed[i]['low'],
        close: +graphDataProcessed[i]['close'],
        volume: +graphDataProcessed[i]['volume']
      };
    });
  };

  const data_temp = generateData();
  console.log(typeof data_temp[1]['open']);
  const data_real = processGraphData();
  const [data] = useState(data_temp);
  console.log(typeof data_real[1]['open']);


  //candlestick tutorial end-----


  return (
    <>
      <h4>Your Balance</h4>

      <Chart data={data} width={chart_width} height={chart_height} />
      <h1>${total}</h1>
    </>
  )
}
