const logger = (state, action) => {
  switch(action.type) {
    case 'DELETE_TRANSACTION':
      return {
        ...state,
        transactions: state.transactions.filter(transaction => transaction.id !== action.payload)
      }
    case 'ADD_TRANSACTION':
      return {
        ...state,
        transactions: [action.payload, ...state.transactions]
      }
    case 'STOCK_SEARCH':
      return {
        ...state,
        graphData_AV_API: state.graphData_AV_API,
        symbol_list: action.payload,
        current_symbol: state.current_symbol
      }
    case 'MAX_API':
      return {
        ...state
      }
    case 'GRAPH_TYPE':
      return {
        ...state,
        graph_type: action.payload
      }
    case 'DISPLAY_GRAPHS':
      return {
        ...state,
        graphData_AV_API: action.payload,
        symbol_list: [],
        current_symbol: action.payload['Meta Data']['2. Symbol'],
        cache_TSDA: action.payload['Time Series (Daily)'] ? action.payload : state.cache_TSDA,
        cache_TS15min: action.payload['Time Series (15min)'] ? action.payload : state.cache_TS15min,
        cache_TS60min: action.payload['Time Series (60min)'] ? action.payload : state.cache_TS60min
      }
    default:
      return state;
  }
}

export default logger;
