import React, {useContext} from 'react';
import styled from "styled-components";
import uuid from 'uuid';
import { GlobalContext } from '../context/GlobalState';


const CSS = styled.div`
  margin: 3%;
  padding: 1%;

  h3{
    font-family: 'Lato', sans-serif; 
    font-size: 38px; 
    font-weight: 300; 
    text-align: left;
    color: #dedfe4;
  }

  .btn {
    font-family: 'Lato', sans-serif; 
    text-align: center;
    width: 100%;
    height: 33%;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;
    color: white;
    width: 256px;
    height: 64px;
    line-height: 64px;
    padding: none;
    transition: all 0.3s;
    span {
      transition: all 0.3s;
      tranform: scale(1, 1);
    }
    color: white;
  }

  .btn::before, .btn::after {
    content: '';
    position: absolute;
    transition: all 0.3s;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 1;
    color: white;
  }

  .btn-one::before {
    left: 4px;
    z-index: 1;

    opacity: 0;
    background: rgba(255, 255, 255, 0.1);
    transform: scale(0.1, 1);
    color: white;
  }

  .btn-one:hover::before {
    opacity: 1;
    transform: scale(1, 1);
    color: white;
  }

  .btn-one::after {
    transition: all 0.3s;
    color: white;
  }

  .btn-one:hover::after {
    transform: scale(1, .1);
    opacity: 0;
    color: white;
  }

  background: #373641;
  border-radius: 10px;
`;

export const AddBacktest = () => {
  const { addBacktest } = useContext(GlobalContext);

  const onSubmit = (indicator) => {
    const newBacktest = {
      id: uuid(),
      indicator
    }
    addBacktest(newBacktest);
  }

  return (
    <CSS>
      <h3>Create a New Strategy</h3>
      <button className="btn btn-one" onClick={() => onSubmit('RSI')}>Relative Strength Index (RSI)</button>    
      <button onClick={() => onSubmit('RSI')}>Relative Strength Index (RSI)</button>
      <button onClick={() => onSubmit('EMA')}>Exponential Moving Average (EMA)</button>
      <button onClick={() => onSubmit('SMA')}>Simple Moving Average (SMA)</button>
      <button onClick={() => onSubmit('BBANDS')}>Bollinger Bands (BB)</button>
    </CSS>
  )
}
