import React, {useContext} from 'react';
import styled from "styled-components";
import uuid from 'uuid';
import { GlobalContext } from '../context/GlobalState';


const CSS = styled.div`
  margin: 3%;
  padding: 1%;

  h3{
    font-family: 'Lato', sans-serif; 
    font-size: 38px; 
    font-weight: 300; 
    text-align: left;
    color: #dedfe4;
  }

  button{
    border: none;
    background: white;

  }
  .btn {
    position: relative;
    color: white;
    width: 256px;
    height: 64px;
    line-height: 64px;
    padding: none;
    transition: all 0.3s;
    span {
      transition: all 0.3s;
      tranform: scale(1, 1);
    }
  }

.btn::before, .btn::after {
  content: '';
  position: absolute;
  transition: all 0.3s;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 1;
}

.btn-one::before {
	left: 4px;
	z-index: 1;

  opacity: 0;
  background: rgba(255, 255, 255, 0.1);
  transform: scale(0.1, 1);
}

.btn-one:hover::before {
  opacity: 1;
  transform: scale(1, 1);
}

.btn-one::after {
  transition: all 0.3s;
  border: 1px solid rgba(255, 255, 255, 0.5);
}

.btn-one:hover::after {
  transform: scale(1, .1);
  opacity: 0;
}

    // margin: 1%;
    // padding: 1%;

    // color: #dedfe4;
    // font-size: 150%;
    // background: transparent;
    // border: solid 1px;
    // border-radius: 10px;


    // &:hover {
    //   background: #3d3d4a;
    //   border: solid transparent 1px;
    //   box-shadow: rgba(0, 0, 0, 0.4) 0px 3px 10px;
    // }
    // &:active {
    //   background: #101010;
    //   border: solid transparent 1px;
    // }



  background: #373641;
  border-radius: 10px;
`;

export const AddBacktest = () => {
  const { addBacktest } = useContext(GlobalContext);

  const onSubmit = (indicator) => {
    const newBacktest = {
      id: uuid(),
      indicator
    }
    addBacktest(newBacktest);
  }

  return (
    <CSS>
      <h3>Create a New Strategy</h3>


          <button className="btn btn-one" onClick={() => onSubmit('RSI')}>Relative Strength Index (RSI)</button>

      <button onClick={() => onSubmit('RSI')}>Relative Strength Index (RSI)</button>
      <button onClick={() => onSubmit('EMA')}>Exponential Moving Average (EMA)</button>
      <button onClick={() => onSubmit('SMA')}>Simple Moving Average (SMA)</button>
      <button onClick={() => onSubmit('BBANDS')}>Bollinger Bands (BB)</button>
    </CSS>
  )
}
