import React, { useContext, useEffect, useState } from 'react';
import Chart from "./chart-components/Chart";

import { GlobalContext } from '../context/GlobalState';
import "../chart.css";


export const GraphSection = () => {
  //grab global variables/functions to call in this file
  const { graphData_AV_API, graphSymbol, current_symbol, graph_type } = useContext(GlobalContext);

  const chart_width = window.screen.availWidth;
  const chart_height = window.screen.availHeight*2/3;

  // Processes update to API call and sends data to candlestick chart.
  const processGraphData = () => {
    let time_series_type;
    if(graphData_AV_API['Time Series (15min)']){
      time_series_type = 'Time Series (15min)';
    }else if(graphData_AV_API['Time Series (60min)']){
      time_series_type = 'Time Series (60min)';
    }else{//if(graphData_AV_API['Time Series (Daily)']){
      time_series_type = 'Time Series (Daily)';
    }
    const time_series_isDaily = time_series_type === 'Time Series (Daily)' ? true : false;
    let split_ratio = 1.0;

    //these two arrays are sus
    let xAxis_times = [];
    let graphDataProcessed = [];
    let i = 0

    let date_counter = 0;
    let previous_date = '';
    let stop_graphing = false;
    // Iterate/translate API call to fit my charts inputs
    for(let myDate in graphData_AV_API[time_series_type]){
      xAxis_times.push(myDate);

      // Counts dates, breaks according to requested graph data
      if( !(previous_date===myDate.substr(0,10)) ){
        console.log(date_counter++, myDate.substr(0,10));
        previous_date = myDate.substr(0,10);

        switch(graph_type){
            case 'day':
                if(date_counter>1) stop_graphing = true;
                break;
            case 'week':
                if(date_counter>5) stop_graphing = true;
                break;
            case 'month':
                if(date_counter>20) stop_graphing = true;
                break;
            case 'quarter':
                if(date_counter>60) stop_graphing = true;
                break;
            case 'year':
                if(date_counter>253) stop_graphing = true;
                break;
            default:
        }
        if(stop_graphing){
          console.log("stop graphing", previous_date, date_counter);
          break;
        } 

      }

      // Some API data open==close==high==low, resulting in candles of size 0x0, resulting in empty graph. 
      // flat_candle ensures candles are never 0x0 (invisible).
      let flat_candle = +graphData_AV_API[time_series_type][myDate]['1. open']=== +graphData_AV_API[time_series_type][myDate]['4. close'] ? 0.001 : 0;

      graphDataProcessed.push({
        "time": i,
        "open": +graphData_AV_API[time_series_type][myDate]['1. open']/split_ratio,
        "close": flat_candle+graphData_AV_API[time_series_type][myDate]['4. close']/split_ratio,
        "high": +graphData_AV_API[time_series_type][myDate]['2. high']/split_ratio,
        "low": +graphData_AV_API[time_series_type][myDate]['3. low']/split_ratio,
        "volume": +graphData_AV_API[time_series_type][myDate][time_series_isDaily ? '6. volume' : '5. volume']
      });
      if(time_series_isDaily) split_ratio *= +graphData_AV_API[time_series_type][myDate]['8. split coefficient'];
      i++;
    }
    graphDataProcessed.reverse();
    return graphDataProcessed;
  };

  // Only for intialization: maybe make it display SPY as default
  const [data, setData] = useState(() => processGraphData());
  // Whenever graphData_AV_API is changed, code below will update graph
  useEffect(() => {
    setData(processGraphData());
  }, [graphData_AV_API]); // eslint-disable-line react-hooks/exhaustive-deps


  if(current_symbol.length>0){
    return (
      <>
        <h1>{current_symbol}</h1>
        <Chart data={data} width={chart_width} height={chart_height} />
        <div className='card mb-1 center'>
          <button onClick={() => graphSymbol(current_symbol,'day', false)} className="text-info bg-dark">
            <p className='text-warning'>1D</p>
          </button>
          <button onClick={() => graphSymbol(current_symbol,'week', false)} className="text-info bg-dark">
            <p className='text-warning'>1W</p>
          </button>
          <button onClick={() => graphSymbol(current_symbol,'month', false)} className="text-info bg-dark">
            <p className='text-warning'>1M</p>
          </button>
          <button onClick={() => graphSymbol(current_symbol,'quarter', false)} className="text-info bg-dark">
            <p className='text-warning'>3M</p>
          </button>
          <button onClick={() => graphSymbol(current_symbol,'year', false)} className="text-info bg-dark">
            <p className='text-warning'>1Y</p>
          </button>
      </div>
      </>
    )
  }else{
    return (
      <>
      </>
    )
  }
}
