import React, { useContext, useRef, useEffect, useState } from 'react';
import { d3, select } from 'd3';
import { GlobalContext } from '../context/GlobalState';

export const Balance = () => {
  const { transactions } = useContext(GlobalContext);

  const amounts = transactions.map(transaction => transaction.amount);

  const total = amounts.reduce((acc, item) => (acc += item), 0).toFixed(2);

  //temp graph d3 example
  const [data] = useState([25,33,44,53,23]);
  const svgRef = useRef();

  // useEffect( () => {
  //   const svg = select(svgRef.current);//now can use all d3 commands and stuff
  //   svg.selectAll("circle").data(data)
  //     .join(
  //       enter => enter.append("circle")     //effects circles we add
  //         .attr("r", value => value)
  //         .attr("cx", value => value*2)
  //         .attr("cy", value => value*2)
  //         .attr("stroke", "red"),
  //       update => update.attr("class", "updated"),    //effects circles that we update(exist in html alr)
  //       exit => exit.remove()                         //delete else, or remove
  //     )                                               //add .attr here to effect ALL circles

  // }, [data]);
  //------end d3 ex code------- also imports
  useEffect( () => {
    const svg = select(svgRef.current);//now can use all d3 commands and stuff
     svg.attr("viewBox", [0, 0, width, height]);

     svg.append("g")
         .call(xAxis);

     svg.append("g")
         .call(yAxis);

     const g = svg.append("g")
         .attr("stroke-linecap", "round")
         .attr("stroke", "black")
       .selectAll("g")
       .data(data)
       .join("g")
         .attr("transform", d => `translate(${x(d.date)},0)`);

     g.append("line")
         .attr("y1", d => y(d.low))
         .attr("y2", d => y(d.high));

     g.append("line")
         .attr("y1", d => y(d.open))
         .attr("y2", d => y(d.close))
         .attr("stroke-width", x.bandwidth())
         .attr("stroke", d => d.open > d.close ? d3.schemeSet1[0]
             : d.close > d.open ? d3.schemeSet1[2]
             : d3.schemeSet1[8]);                                         //add .attr here to effect ALL circles

  }, [data]);




  //candlestick tutorial end-----


  return (
    <>
      <h4>Your Balance</h4>

      <svg ref = {svgRef}> </svg>
      <h1>${total}</h1>
    </>
  )
}
