import React, { useState } from "react";

import Candle from "./Candle";
import CrossHairs from "./CrossHairs";

const Chart = props => {
  const { data, width: chart_width, height: chart_height } = props;
  // let { last_bar_idx = 0, bars_wide = 40 } = props;

  // last_bar_idx should default to the last bar in the data, or else be sure passed-in value doesn't exceed the last bar
  // last_bar_idx = last_bar_idx > 0 ? Math.min(last_bar_idx, data.length - 1) : data.length - 1;

  const [mouseCoords, setMouseCoords] = useState({
    x: -50,
    y: -50
  });

  // let mouseCoords = {
  //   x: 0,
  //   y: 0
  // };

  // const setMouseCoords = (x, y) => {
  //   mouseCoords = { x, y };
  // };

  // find the high and low bounds of all the bars being sidplayed
  const dollar_high = Math.max.apply(Math, data.map(bar => bar.high)) * 1.15;
  const dollar_low = Math.min.apply(Math, data.map(bar => bar.low)) * 0.85;
  const chart_dims = {
    pixel_width: chart_width,
    pixel_height: chart_height,
    dollar_high,
    dollar_low,
    dollar_delta: dollar_high - dollar_low
  };

  // toFixed rounds number to 2 decimal places
  const dollarAt = pixel => {
    const dollar =
      (Math.abs(pixel - chart_dims.pixel_height) / chart_dims.pixel_height) *
        chart_dims.dollar_delta +
      chart_dims.dollar_low;

    return pixel > 0 ? dollar.toFixed(2) : "-";
  };

  // Gets candlestick data at x position
  const candleDataAt = pixel => {
    if(pixel<=0 || pixel >= chart_dims.pixel_width) return "-";
    const candle = data[Math.floor((pixel/ chart_dims.pixel_width) * data.length)];

    return candle;
  };

  const candleChange = pixel => {
    if(pixel<=0 || pixel >= chart_dims.pixel_width) return "-";
    const candle = data[Math.floor((pixel/ chart_dims.pixel_width) * data.length)];
    const change = ((candle['close']/candle['open'])-1) * 100;
    const posNeg = change > 0 ? '+' : '-';
    return posNeg + Math.abs(change).toFixed(2) + '%';
  };

  const pixelFor = dollar => {
    console.log((dollar - chart_dims["dollar_low"]) / chart_dims["dollar_delta"]);
    return Math.abs(
      ((dollar - chart_dims["dollar_low"]) / chart_dims["dollar_delta"]) *
        chart_dims["pixel_height"] -
        chart_dims["pixel_height"]
    );
  };

  const onMouseLeave = () => {
    setMouseCoords({
      x: -50,
      y: -50
    });
  };

  const onMouseMoveInside = e => {
    //for date(x axis), calc using dates[x cord/width] , create dates[]
    setMouseCoords({
      x:
        e.nativeEvent.x -
        Math.round(e.currentTarget.getBoundingClientRect().left),
      y:
        e.nativeEvent.y -
        Math.round(e.currentTarget.getBoundingClientRect().top)
    });
  };

  const onMouseClickInside = e => {
    console.log(`Click at ${e.nativeEvent.offsetX}, ${e.nativeEvent.offsetY}`);
  };

  // calculate the candle width
  const candle_width = Math.floor((chart_width / data.length) * 0.7);
  return (
    <svg
      width={chart_width*1.025 /* Extra width to fit price */}
      height={chart_height}
      className="chart"
      onMouseMove={onMouseMoveInside}
      onClick={onMouseClickInside}
      onMouseLeave={onMouseLeave}
    >
      {data.map((bar, i) => {
        const candle_x = (chart_width / (data.length + 1)) * (i + 1);
        return (
          <Candle
            key={i}
            data={bar}
            x={candle_x}
            candle_width={candle_width}
            pixelFor={pixelFor}
          />
        );
      })}

      <CrossHairs x={mouseCoords.x} y={mouseCoords.y} chart_dims={chart_dims} />

      {/* Price */}
      <rect x={(chart_width*1.025)-(1+dollarAt(mouseCoords.y).length)*10} y={mouseCoords.y-12} width={(1+dollarAt(mouseCoords.y).length)*10} height='25' fill="white" rx='9' ry='9'>{/* background color for price */}</rect>
      <text x={5 + (chart_width*1.025)-(1+dollarAt(mouseCoords.y).length)*10} y={mouseCoords.y+5} fill='black' fontSize="15">
        ${dollarAt(mouseCoords.y)}
      </text>
      {/* Open/High */}
      <text x='16' y='30' fontSize="20">
        <tspan fill='grey'>Open</tspan> <tspan fill='white'>${candleDataAt(mouseCoords.x)['open']}  </tspan>
        <tspan fill='grey'>High</tspan> <tspan fill='white'>${candleDataAt(mouseCoords.x)['high']}</tspan>
      </text>
      {/* Low/Close */}
      <text x='16' y='54' fontSize="20">
        <tspan fill='grey'>Close</tspan> <tspan fill='white'>${candleDataAt(mouseCoords.x)['close']}  </tspan>
        <tspan fill='grey'>Low</tspan> <tspan fill='white'>${candleDataAt(mouseCoords.x)['low']}</tspan>
      </text>
      {/* Volume/%change */}
      <text x='16' y='76' fontSize="20">
        <tspan fill='grey'>Volume</tspan> <tspan fill='white'>{mouseCoords.x > 0 ?(candleDataAt(mouseCoords.x)['volume']/1000).toFixed(2) + 'K': ''}  </tspan>
        <tspan fill='grey'>Change</tspan> <tspan fill='white'>{candleChange(mouseCoords.x)}</tspan>
      </text>
      {/* Date Time */}
      <text x='16' y='110' fill='white' fontSize='30'>
        {candleDataAt(mouseCoords.x)['xAxis']} {mouseCoords.x > 0 ? 'EST' : ''} 
      </text>
    </svg>
  );
};

export default Chart;
