import React, {useContext} from 'react';
import { GlobalContext } from '../context/GlobalState';

export const Backtest = ({ backtest }) => {
  const { deleteBacktest } = useContext(GlobalContext);

  const sign = backtest.amount < 0 ? '-' : '+';

  return (
    <li className={backtest.amount < 0 ? 'minus' : 'plus'}>
      {backtest.text} <span>{sign}${Math.abs(backtest.amount)}</span>
      <button 
          onClick={() => deleteBacktest(backtest.id)} className="btn-primary">x

      </button>
    </li>
  )
}
