import React, { useContext } from 'react';
import styled from "styled-components";
import { Backtest } from './Backtest';

import { GlobalContext } from '../context/GlobalState';

// CSS animated gradient background, choose the 4 colors it rotates thry, define how long it rotates(60s)
const CSS = styled.div`
  background: linear-gradient(-45deg, #DCDCDC, #F0FFF0, #F5FFFA, #FFDAB9);
  background-size: 400% 400%;
  animation: gradient 60s ease infinite;

  @keyframes gradient {
    0% {
      background-position: 0% 50%;
    }
    50% {
      background-position: 100% 50%;
    }
    100% {
      background-position: 0% 50%;
    }
  }
`;

export const BacktestList = () => {
  const { backtests } = useContext(GlobalContext);

  return (
    <>
      <h3>Strategy Tester</h3>
      {backtests.map(backtest => (<Backtest key={backtest.id} backtest={backtest} />))}
    </>
  )
}
