import React, {useState, useContext} from 'react'
import { GlobalContext } from '../context/GlobalState';

export const AddBacktest = () => {
  const [text, setText] = useState('');
  const [amount, setAmount] = useState(0);

  const { addBacktest } = useContext(GlobalContext);

  const onSubmit = e => {
    e.preventDefault();

    const newBacktest = {
      id: Math.floor(Math.random() * 100000000),
      text,
      amount: +amount
    }

    addBacktest(newBacktest);
  }

  const onSubmit1 = (indicator) => {
    const newBacktest = {
      id: Math.floor(Math.random() * 100000000),
      text: indicator,
      amount: 420
    }

    addBacktest(newBacktest);
  }

  return (
    <>
      <h3>Create a New Strategy</h3>
      <button onClick={() => onSubmit1('RSI')}>Relative Strength Index (RSI)</button>
      <button onClick={() => onSubmit1('EMA')}>Exponential Moving Average (EMA)</button>
      <button onClick={() => onSubmit1('SMA')}>Simple Moving Average (SMA)</button>
      <button onClick={() => onSubmit1('BB')}>Bollinger Bands (BB)</button>
