import React, {useState, useContext, useEffect } from 'react'
import { GlobalContext } from '../context/GlobalState';

export const Backtest = ({ backtest }) => {
  const defaultTimePeriod = () => {
    switch(backtest.indicator){
      case 'RSI': return 14;
      case 'EMA': return 9;
      case 'SMA': return 9;
      case 'BB': return 20;
      default: return 24;
    }
  }
  const defaultBuyStrat = () => {
    switch(backtest.indicator){
      case 'RSI': return 30;
      case 'EMA': return -3;
      case 'SMA': return -3;
      case 'BB': return -3;
      default: return -3;
    }
  }

  const [seriesType, setSeriesType] = useState('open');
  const [timePeriod, setTimePeriod] = useState(defaultTimePeriod());
  const [stdDeviation, setStdDeviation] = useState(2);

  const [maxBags, setMaxBags] = useState(5);
  const [initalBalance, setInitialBalance] = useState(10000);
  const [initalBuyType, setInitalBuyType] = useState('usd');
  const [initalBuyAmount, setInitalBuyAmount] = useState(1500);
  const [buyStrategyValue, setBuyStrategyValue] = useState(defaultBuyStrat());
  const [buyStrategyType, setBuyStrategyType] = useState('middle'); // Used for Bollinger upper/middle/lower
  const [sellStrategyValue, setSellStrategyValue] = useState(1);

  const { deleteBacktest, current_symbol, cache_TSDA, cache_TSDA_function } = useContext(GlobalContext);

  const dropdownItems = (items) => {
    return items.map(item => <option value={item.value} key={item.name}>{item.name}</option>);
  }

  useEffect(() => {
    console.log("updated cache_Tsda");
  }, [cache_TSDA]); // eslint-disable-line react-hooks/exhaustive-deps

  async function runBacktests(paperAPICall, pairSettings) {
    let stockPriceData;
    let response;

    // Ensure user has selected a stock
    if(!current_symbol){
      alert("Please select a stock to test your strategy on.");
      return;
    }

    // Ensure API json for cache_TSDA exists
    if(!cache_TSDA['Meta Data']){
      response = await fetch(`https://www.alphavantage.co/query?function=TIME_SERIES_DAILY_ADJUSTED&symbol=${current_symbol}&outputsize=full&apikey=CQ5JZQYM4DG0OODA`);
      stockPriceData = await response.json();
      cache_TSDA_function(stockPriceData);

      // Stop if MAX API
      if(stockPriceData.Note) return;
    }else{
      stockPriceData = cache_TSDA;
    }

    console.log(paperAPICall, pairSettings, current_symbol, cache_TSDA, stockPriceData);
  }

  const onSubmit = () => {

    const paperAPICall = { 
      // Relavent variables that are global: current_symbol, interval(daily), backtest.indicator(ex, RSI)
      seriesType: seriesType,
      timePeriod: +timePeriod,
      stdDeviation: +stdDeviation
    };

    const pairSettings = {
      maxBags: +maxBags,
      initalBalance: +initalBalance,
      initalBuyType: initalBuyType,
      initalBuyAmount: +initalBuyAmount,
      buyStrategyValue: +buyStrategyValue,
      buyStrategyType, // used for Bollinger upper/middle/lower
      sellStrategyValue: +sellStrategyValue
    }

    // Ensure no trivial strategies are submitted
    if(pairSettings.maxBags<1 || pairSettings.initalBalance<1 || pairSettings.initalBuyAmount<1 || pairSettings.sellStrategyValue<0 || paperAPICall.timePeriod<1){
      // Bags, balance, timeperiod, buy amount, sell gain% must be positive
      alert("Please enter valid inputs. Use positive integers for the following: Max bags, Sell percentage, Time period, Buy amount, Inital Balance.");
    }else if(pairSettings.initalBuyType==='percent' && (pairSettings.initalBuyAmount<1 || pairSettings.initalBuyAmount>100)){
      // if(buy type is percent) buy amount must be (0-100]
      alert("Buy amount percentage must be 1-100%");
    }else if (pairSettings.initalBuyAmount>pairSettings.initalBalance){
      // inital buy must be < inital balance
      alert("Inital buys must be less than or equal to inital balance.");
    }else if(backtest.indicator==='RSI' && (pairSettings.buyStrategyValue<1 || pairSettings.buyStrategyValue>99)){
      // Ensure no trivial strategy specific settings are submitted <connect to previous if chain thing>
      alert("RSI values must be between 1-99");
    }else if(backtest.indicator==='BB' && paperAPICall.stdDeviation<0.5){
      // Ensure no trivial BB std deviation
      alert("Standard Deviation must be greater than or equal to 0.5.");
    }else{
      // Calculation inputs valid, run backtest algorithm
      runBacktests(paperAPICall, pairSettings);
    }
  }

  let indicatorSettingDOM = null;
  switch(backtest.indicator){
    case 'RSI':
      indicatorSettingDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='14' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'EMA':
      indicatorSettingDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='9' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'SMA':
      indicatorSettingDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='9' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>
      </>);
      break;
    case 'BB':
      indicatorSettingDOM = (<> 
        {/* Time Period(# candles in calc) */}
        <label>
          Time Period:
          <input type='number' placeholder='20' onChange={(e) => setTimePeriod(e.target.value)} />
        </label>

        {/* Standard deviation for both top/bot lines */}
        <label>
          Standard Deviation for upper/lower lines:
          <input type='number' placeholder='2' onChange={(e) => setStdDeviation(e.target.value)} />
        </label>
      </>);
      break;
    default:
      indicatorSettingDOM = (<div>indicaator setting dom default</div>);

  }

  let buyStrategyDOM = null;
  switch(backtest.indicator){
    case 'RSI':
      buyStrategyDOM = (<> 
        {/* Buy Strategy RSI */}
        <label>
          Buy at RSI {"<"}
          <input type='number' placeholder='30' onChange={(e) => setBuyStrategyValue(e.target.value)} />
        </label>
      </>);
      break;
    case 'EMA':
      buyStrategyDOM = (<> 
        {/* Buy strategy % away */}
        <label>
          Buy at price 
          <input type='number' placeholder='-3' onChange={(e) => setBuyStrategyValue(e.target.value)} />
          % away from EMA line. 
          <br/>
          (3 = 3% above, -3 = 3% below)
        </label>
      </>);
      break;
    case 'SMA':
      buyStrategyDOM = (<> 
        {/* Buy strategy % away */}
        <label>
          Buy at price 
          <input type='number' placeholder='-3' onChange={(e) => setBuyStrategyValue(e.target.value)} />
          % away from SMA line. 
          <br/>
          (3 = 3% above, -3 = 3% below)
        </label>
      </>);
      break;
    case 'BB':
      buyStrategyDOM = (<> 
        {/* Buy strategy % away (BB) */}
        <label>
          Band to follow:  
          <select onChange={(e) => setBuyStrategyType(e.target.value)}>
            {dropdownItems([
              {name:'Upper', value:'Real Upper Band'},
              {name:'Middle', value:'Real Lower Band'},
              {name:'Lower', value:'Real Middle Band'}])}
          </select>
        </label>
        <label>
          Buy at price 
          <input type='number' placeholder='-3' onChange={(e) => setBuyStrategyValue(e.target.value)} />
          % away from selected line. 
          <br/>
          (3 = 3% above, -3 = 3% below)
        </label>
      </>);
      break;
    default:
      buyStrategyDOM = (<div>buy strat dom default</div>);

  }

  return (
    <div className='row mx-4 my-2'>
      <div className='card w-25'>
        {/* Beginning of Settings */}
        {backtest.indicator} Settings

        {/* series type */}
        <label>
          Select Series Type:
          <select onChange={(e) => setSeriesType(e.target.value)}>
            {dropdownItems([
              {name:'open', value:'open'},
              {name:'close', value:'close'},
              {name:'high', value:'high'},
              {name:'low', value:'low'}])}
          </select>
        </label>

        {indicatorSettingDOM}

        <br/>

        <label>Paper Bot Settings</label>
        {/* Max number bags */}
        <label>
          Max number of bags:
          <input type='number' placeholder='5' onChange={(e) => setMaxBags(e.target.value)} />
        </label>

        {/* Inital balance */}
        <label>
        Inital balance (USD):
          <input type='number' min='0' placeholder='10000' onChange={(e) => setInitialBalance(e.target.value)} />
        </label>

        {/* Inital buy (type/amount) */}
        <label>
        Inital buy type:
          <select onChange={(e) => setInitalBuyType(e.target.value)}>
            {dropdownItems([
                {name:'($) USD', value:'usd'},
                {name:'(%) Percent of Balance', value:'percent'},
                {name:'(#) Number of Stocks', value:'numStocks'}])}
            </select>
        </label>
        <label>
        Inital buy amount:
          <input type='number' placeholder='1500' onChange={(e) => setInitalBuyAmount(e.target.value)} />
        </label>

        {buyStrategyDOM}

        {/* Sell Strategy */}
        <label>
          Sell at 
          <input type='number' placeholder='1' onChange={(e) => setSellStrategyValue(e.target.value)} />
          % profit.
        </label>

        {/* submit button */}
        <button onClick={() => onSubmit()} className="btn-secondary w-25">Calculate</button>


        {/* close button */}
        <button onClick={() => deleteBacktest(backtest.id)} className="btn-primary w-25">x</button>
      </div>
      {/* end of settings, beginning of results */}

      <div className='card w-75'>results baby</div>

      {/* End of results */}
    </div>
  );
}
