import React from 'react';
import styled from "styled-components";

  const CSS = styled.div`

    .trade-card{
      margin-bottom: 10px;
      font-size: 12px;

      display:block;
      overflow:auto;

      box-shadow: rgba(0, 0, 0, 0.25) 0px 0.0625em 0.0625em, rgba(0, 0, 0, 0.25) 0px 0.125em 0.5em, rgba(255, 255, 255, 0.1) 0px 0px 0px 1px inset;
      background: #3d3d4a;  
    }
    .trade-card-sub{
      float: left;
      width: 33.33%;
      padding: 1.25%;    
    }
    .result-item{
      border-bottom: 2px solid #6464a3;
      height: auto;
      margin-bottom: 4%;
      padding-bottom: 1%;
    }
    .result-title{
      text-align: left;
      color: #898996;
      font-size: 12px;
      width: 40%;
    }
    .result-output{
      text-align: right;
      color: #dedfe4;
      font-size: 14px;
      width: 60%;
    }
    .result-title-profit{
      text-align: left;
      color: #898996;
      font-size: 12px;
      width: 1%;

    }
    .result-output-profit{
      text-align: right;
      color: #dedfe4;
      font-size: 14px;
      width: 99%;

      /* Tooltip begin */
      [data-tooltip] {
        position: relative;
        z-index: 10;
      }

      /* Positioning and visibility settings of the tooltip */
      [data-tooltip]:before,
      [data-tooltip]:after {
        position: absolute;
        visibility: hidden;
        opacity: 0;
        left: 50%;
        bottom: calc(100% + 5px); /* 5px is the size of the arrow */
        pointer-events: none;
        transition: 0.2s;
        will-change: transform;
      }

      /* The actual tooltip with a dynamic width */
      [data-tooltip]:before {
        content: attr(data-tooltip);
        padding: 10px 18px;
        min-width: 50px;
        max-width: 300px;
        width: max-content;
        width: -moz-max-content;
        border-radius: 6px;
        font-size: 14px;
        background-color: rgba(59, 72, 80, 0.9);
        background-image: linear-gradient(30deg,
          rgba(59, 72, 80, 0.44),
          rgba(59, 68, 75, 0.44),
          rgba(60, 82, 88, 0.44));
        box-shadow: 0px 0px 24px rgba(0, 0, 0, 0.2);
        color: #fff;
        text-align: center;
        white-space: pre-wrap;
        transform: translate(-50%, -5px) scale(0.5);
      }

      /* Tooltip arrow */
      [data-tooltip]:after {
        content: '';
        border-style: solid;
        border-width: 5px 5px 0px 5px; /* CSS triangle */
        border-color: rgba(55, 64, 70, 0.9) transparent transparent transparent;
        transition-duration: 0s; /* If the mouse leaves the element, 
                                    the transition effects for the 
                                    tooltip arrow are "turned off" */
        transform-origin: top;   /* Orientation setting for the
                                    slide-down effect */
        transform: translateX(-50%) scaleY(0);
      }

      /* Tooltip becomes visible at hover */
      [data-tooltip]:hover:before,
      [data-tooltip]:hover:after {
        visibility: visible;
        opacity: 1;
      }
      /* Scales from 0.5 to 1 -> grow effect */
      [data-tooltip]:hover:before {
        transition-delay: 0.3s;
        transform: translate(-50%, -5px) scale(1);
      }
      /* 
        Arrow slide down effect only on mouseenter (NOT on mouseleave)
      */
      [data-tooltip]:hover:after {
        transition-delay: 0.5s; /* Starting after the grow effect */
        transition-duration: 0.2s;
        transform: translateX(-50%) scaleY(1);
      }

/********testing****/
[data-tooltip-left]:before,
[data-tooltip-left]:after {
  left: auto;
  right: calc(100% + 5px);
  bottom: 50%;
}

/* Tooltip */
[data-tooltip-left]:before {
  transform: translate(-5px, 50%) scale(0.5);
}
[data-tooltip-left]:hover:before {
  transform: translate(-5px, 50%) scale(1);
}

/* Arrow */
[data-tooltip-left]:after {
  border-width: 5px 0px 5px 5px;
  border-color: transparent transparent transparent rgba(55, 64, 70, 0.9);
  transform-origin: left;
  transform: translateY(50%) scaleX(0);
}
[data-tooltip-left]:hover:after {
  transform: translateY(50%) scaleX(1);
}
/**testint*******************/


      /* End of tooltips */
    }
  `;

const Trade = props => {
  const { trade } = props;

  return (
    <CSS>
      <div className="trade-card">
        <div className="trade-card-sub">
          <div className="result-item">
            <label className="result-title" data-tooltip="Date when position was opened.">Buy Date: </label>
            <label className="result-output">{trade.buyDate}</label>
          </div>
          <div className="result-item">
            <label className="result-title" data-tooltip="Stock price when position was opened.">Buy Price: </label>
            <label className="result-output">{trade.buyPrice}</label>
          </div>
          <div className="result-item">
            <label className="result-title" data-tooltip="Technical indicator value for when position was opened.">Indicator at Buy: </label>
            <label className="result-output">{trade.indicatorAtBuy}</label>
          </div>
        </div>
        <div className="trade-card-sub">
          <div className="result-item">
            <label className="result-title" data-tooltip="Date when position was close.">Sell Date:</label>
            <label className="result-output">{trade.sellDate}</label>
          </div>
          <div className="result-item">
            <label className="result-title" data-tooltip="Stock price when position was closed.">Sell Price: </label>
            <label className="result-output">{trade.sellPrice}</label>
          </div>
          <div className="result-item">
            <label className="result-title" data-tooltip="Technical indicator value for when position was closed."data-tooltip-left="">Indicator at Sell: </label>
            <label className="result-output">{trade.indicatorAtSell}</label>
          </div>
        </div>
        <div className="trade-card-sub">
          <div className="result-item">
            <label className="result-title" data-tooltip="Number of shares in position.">Num of Shares:</label>
            <label className="result-output"> {trade.numShares.toFixed(2)}</label>
          </div>
          <div className="result-item">
            <label className="result-title-profit" data-tooltip="Profit from opening and closing this position.">Profit: </label>
            <label className="result-output-profit">${trade.profitUSD.toFixed(2)}({trade.profitPercent>0?"+":""}{(trade.profitPercent*100).toFixed(2)}%)</label>
          </div>
          <div className="result-item">
            <label className="result-title" data-tooltip="Number of days between opening and closing this position."> Num Candles in Trade: </label>
            <label className="result-output">{trade.candleLength}</label>
          </div>
        </div>
      </div>

    </CSS>
  )
}

export default Trade;
